<body>
	<div id="all">
		<h1><?php
		$part = str_replace($workingDir, "", $path);
		if ($part == "") {
			echo("home");
			$shortpath = "";
		} else {
			$cpath = "";
			$link = "<a href=\"" . $_SERVER["PHP_SELF"] . "\">home</a>";
			$parts = explode("/", $part);
			$last = array_pop($parts);

			foreach ($parts as $folder) {
				if (!strlen($folder))
					continue;

				$cpath .= "/" . $folder;
				$link .= " / <a href=\"?p=$cpath\">$folder</a>";
			}

			$link .= " / $last";
			
			echo($link);
			$shortpath = $part;
		}
		?></h1>

		
		<?php
		if ($lockedOut) {
			echo("<h2>Please enter the password for this folder</h2>");
			echo("
				<form action=\"\" method=\"post\">
					<input name=\"password\" placeholder=\"Password\" type=\"password\" autofocus />
				</form>
			");
			if ($lockedOut == 2)
				echo("<h3>Incorrect password</h3>");
		} else {
			if (count($directories) + count($files) > 0) {
				echo("<ul>");
				foreach ($directories as $directory => $items) {
					echo("
						<li class=\"folder\">
							<a href=\"?p=$shortpath/$directory\">
								$directory <span>$items items</span>
							</a>
						</li>"
					);
				}

				foreach ($files as $file => $size) {
					echo("
						<li class=\"file\">
							<a href=\"$shortpath/$file\" download>
								$file <span>$size bytes</span>
							</a>
						</li>"
					);
				}
				echo("</ul>");
			} else {
				echo("<h2>Folder is empty! :(</h2>");
			}
		}
		?>
		</ul>
